const initialState = {
  buttonClicked: false,
  pageLoaded: false,
  authCode: '',
  accessToken: '',
  refreshToken: '',
  tokenExpirationSeconds: 0,
}

const customMiddleWare = store => next => action => {
  
  // switch(action.type) {
  //   case 'PAGE_LOAD_INITIATED':
  //     fetch('/api/authcode')
  //       .then(res => res.json())
  //       .then(authCode => window.location.assign(authCode))
  //     break;
  //   case 'PAGE_LOAD_SUCCESSFUL':
  //     console.log(action)
    
  //     fetch('/api/accesstoken', {
  //       method: 'POST',
        
  //     })
  //     break;
  //   default: 
  // }

  return next(action)
}

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case 'PAGE_LOAD_SUCCESSFUL':
      return {
        ...state,
        pageLoaded: true,
        authCode: action.payload.authCode
      };
    case 'BUTTON_CLICKED':
      return {
        ...state,
        buttonClicked: true
      };
    case 'ACCESS_TOKEN_RECEIVED':
      return {
        ...state,
        accessToken: action.payload.accessToken,
        refreshToken: action.payload.refreshToken,
        tokenExpirationSeconds: action.payload.tokenExpirationSeconds
      }
    default:
      return state;
  }

};

export {customMiddleWare, reducer}
