import sfmcHelper from './sfmcHelper'

const initialState = {
  buttonClicked: false,
  pageLoaded: false,
  authCode: '',
  accessToken: '',
}

const customMiddleWare = store => next => action => {
  switch(action.type) {
    case 'PAGE_LOAD_INITIATED':
      return sfmcHelper.getAuthCode()
    case 'PAGE_LOAD_SUCCESSFUL':
      return sfmcHelper.getAccessToken(action.payload.authCode)
  }
  
  return next(action)
}

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case 'PAGE_LOAD_SUCCESSFUL':
      return {
        ...state,
        pageLoaded: true
      };
    case 'BUTTON_CLICKED':
      return {
        ...state,
        buttonClicked: true
      };
    case 'ACCESS_TOKEN_RECEIVED':
      return {
        ...state,
        pageLoaded: true,
        accessToken: action.payload.accessToken
      }
    default:
      return state;
  }

};

export {customMiddleWare, reducer}
