import axios from 'axios'

export const fetchAuthCode = () => {

  return async (dispatch, getState) => {
    let response = await axios('/api/authcode')
    
    dispatch({
      type: 'FETCH_AUTHORIZATION_CODE',
      payload: response.data
    })
  }
}
