import { initialState } from './store';

export const reducer = (state = initialState, action) => {
  switch (action.type) {
    case 'PAGE_LOAD_SUCCESSFUL':
      return {
        ...state,
        pageLoaded: true
      };
    case 'BUTTON_CLICKED':
      return {
        ...state,
        buttonClicked: true
      };
    default:
      return state;
  }

};
